// Enhanced function to make authenticated requests with proper JWT headers and retry logic
export const makeAuthenticatedRequest = async <T>(
  operation: () => Promise<T>,
  operationType: string = 'unknown',
  maxRetries: number = 3
): Promise<T> => {
  let lastError: any = null;
  
  for (let attempt = 0; attempt < maxRetries; attempt++) {
    try {
      if (!currentJWTToken) {
        console.warn(`[makeAuthenticatedRequest] ‚ö†Ô∏è No JWT token available for ${operationType} (attempt ${attempt + 1})`);
      } else {
        console.log(`[makeAuthenticatedRequest] üîê Making authenticated request for: ${operationType} (attempt ${attempt + 1})`);
      }
      
      // Create a new client instance with JWT headers for this specific request
      if (currentJWTToken) {
        const authenticatedClient = createClient<Database>(SUPABASE_URL, SUPABASE_PUBLISHABLE_KEY, {
          auth: {
            persistSession: false,
            autoRefreshToken: true,
          },
          global: {
            headers: {
              'Authorization': `Bearer ${currentJWTToken}`
            }
          }
        });

        console.log(`[makeAuthenticatedRequest] üîê Using authenticated client for ${operationType} (attempt ${attempt + 1})`);
        
        // Replace the global supabase instance temporarily for this operation
        const originalFrom = supabase.from.bind(supabase);
        const originalRpc = supabase.rpc.bind(supabase);
        
        // Override methods to use authenticated client
        (supabase as any).from = authenticatedClient.from.bind(authenticatedClient);
        (supabase as any).rpc = authenticatedClient.rpc.bind(authenticatedClient);
        
        try {
          const result = await operation();
          return result;
        } finally {
          // Restore original methods
          (supabase as any).from = originalFrom;
          (supabase as any).rpc = originalRpc;
        }
      }
      
      return await operation();
    } catch (error: any) {
      lastError = error;
      console.error(`[makeAuthenticatedRequest] ‚ùå Attempt ${attempt + 1} failed for ${operationType}:`, error);
      
      // If it's a JWT expired error and we have more attempts, wait a bit and retry
      if (error?.code === 'PGRST301' || error?.message?.includes('JWT expired')) {
        if (attempt < maxRetries - 1) {
          console.log(`[makeAuthenticatedRequest] üîÑ JWT expired, retrying in ${(attempt + 1) * 1000}ms...`);
          await new Promise(resolve => setTimeout(resolve, (attempt + 1) * 1000));
          continue;
        }
      }
      
      // For other errors, don't retry
      if (!error?.message?.includes('JWT expired')) {
        throw error;
      }
    }
  }
  
  // If we've exhausted all retries, throw the last error
  throw lastError;
};

