// Enhanced function to set Clerk JWT token using Supabase's setSession
export const setClerkToken = async (token: string | null) => {
  try {
    console.log('[setClerkToken] üîÑ Setting Clerk JWT token...');
    
    if (token) {
      // Validate token format before setting
      const parts = token.split('.');
      if (parts.length !== 3) {
        console.error('[setClerkToken] ‚ùå Invalid JWT format - token does not have 3 parts');
        return false;
      }

      // Try to decode the payload to validate
      try {
        const payload = JSON.parse(atob(parts[1]));
        console.log('[setClerkToken] üîç JWT payload preview:', {
          sub: payload.sub,
          iss: payload.iss,
          aud: payload.aud,
          exp: payload.exp,
          iat: payload.iat,
          role: payload.role
        });
      } catch (e) {
        console.warn('[setClerkToken] ‚ö†Ô∏è Could not decode JWT payload for validation:', e);
      }

      currentJWTToken = token;
      
      // Use Supabase's setSession method to properly set the JWT
      try {
        await supabase.auth.setSession({
          access_token: token,
          refresh_token: '', // We don't use refresh tokens with Clerk
        });
        
        console.log('[setClerkToken] ‚úÖ Clerk JWT token set via setSession');
      } catch (authError) {
        console.warn('[setClerkToken] ‚ö†Ô∏è setSession method failed:', authError);
        return false;
      }
      
      console.log('[setClerkToken] ‚úÖ Clerk JWT token set successfully');
      
      // Set auth for realtime if needed
      if (supabase.realtime) {
        supabase.realtime.setAuth(token);
        console.log('[setClerkToken] üîÑ Realtime auth updated');
      }
      
      return true;
    } else {
      currentJWTToken = null;
      console.log('[setClerkToken] ‚ùå Clerk JWT token cleared');
      
      // Clear auth session
      await supabase.auth.signOut();
      
      // Clear realtime auth
      if (supabase.realtime) {
        supabase.realtime.setAuth(null);
        console.log('[setClerkToken] üîÑ Realtime auth cleared');
      }
      
      return true;
    }
  } catch (error) {
    console.error('[setClerkToken] ‚ùå Error setting Clerk JWT token:', error);
    return false;
  }
};

